import React from 'react';
import { MenuList } from '../helpers/MenuList'
import MenuItem from '../components/MenuItem';
import '../styles/Menu.css'


const imgs = [
    { id: 1, src: "../assets/sd-item.png", caption: "Image 1" },
    { id: 2, src: "../assets/sd-item2.png", caption: "Image 2" },
    { id: 3, src: "../assets/sd-item3.png", caption: "Image 3" },
  ];

  function ImageGrid({ images }) {
    function handleClick(id) {
      console.log(`Clicked image ${id}`);
    }

    return (
      <div>
        {images.map((image) => (
          <div key={image.id}>
            <img src={image.src} alt={image.caption} />
            <button onClick={() => handleClick(image.id)}>Click me</button>
          </div>
        ))}
      </div>
    );
  }


function Menu() {
    return (
        <div className='menu'>

            <h3 className='menuTitle'>Our Menu</h3>
            <div className='menuList'>{MenuList.map((menuItem, key)=>{
                return <MenuItem 
                key={key}
                image ={menuItem.image} 
                name={menuItem.name} 
                price={menuItem.price} />
            })}
            </div>
            <button>Add to Cart</button>
        </div>
    );
}

export default Menu;
